import styled from 'styled-components'

const ButtonCompoment = styled.a`
  display: inline-block;
  color: #CCD6F6;
  background: #182A45;
  font-size: 20px;
  padding: 12px 18px;
  border: 1px solid transparent;
  transition: all 0.5s ease;
  &:hover{
    transform: translateY(-5px);
    box-shadow: 4px 8px 20px 0px rgb(0 0 0 / 38%);
    color: #64FFDA;
    border: 1px solid rgb(100 255 218 / 0.03);
    transition: all 0.5s ease;
  }
`
export default ButtonCompoment
