import styled from 'styled-components'

export const FooterComponent = styled.footer`
  width: 100%;
  margin-top: 20px;
  h2{
    color:#CCD6F6;
    font-weight: 400;
    font-size: 36px;
    margin-top: 80px;
    margin-bottom: 22px;
  }
`

export const FooterLinksGrid = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
`
