import styled from 'styled-components'

const LinkItem = styled.div`
  p{
    color: #CCD6F6;
    font-weight: 400;
    font-size: 16px;
  }
  ul{
    list-style: none;
    display: flex;
    li{
      margin-right: 8px;
      a{
        color: #66708D;
        font-size: 16px;
        &:hover{
          color: #64FFDA;
        }
      }
    }
  }
`

export default LinkItem
