import styled from 'styled-components'

const Link = styled.a`
  color: ${props => props.corTexto || "palevioletred"};
  text-decoration: none;
  position: relative;
  display: inline-block;
  font-size: 18px;

  &::after {
    content: '';
    width: 100%;
    height: 3px;
    background: #64FFDA;
    display: block;
    position: absolute;
    top: 0;
    left: 0;
  }

  &:hover {
    color: #fff;
    &::after {
      height: 35px;
    }
  }
`

export default Link
