import styled from 'styled-components'

export const FooterComponent = styled.footer`
  width: 100%;
  margin-top: 20px;
  h2{
    color:#CCD6F6;
    font-weight: 400;
    font-size: 36px;
    margin-top: 80px;
    margin-bottom: 22px;
  }
`

export const FooterLinksGrid = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  align-items: flex-end;
  gap: 15px;
  margin-top: 90px;

  div:last-child{
    justify-self: end;
  }

  @media (max-width: 900px){
    grid-template-columns: repeat(2, 1fr);
    align-items: flex-end;

    div:nth-child(2){
      justify-self: end;
      text-align: right;
    }
  }

  @media (max-width: 600px){
    grid-template-columns: repeat(1, 1fr);
    div{
      margin-bottom: 20px;
    }
    div:last-child{
      justify-self: initial;
    }
    div:nth-child(2){
      justify-self: initial;
      text-align: initial;
    }
  }
`
export const CopyRight = styled.div`
  span{
    color: #CCD6F6;
    font-size: 64px;
    display: block;
    line-height: 0.8;
  }
  small{
    color: #66708D;
    font-size: 10px;
  }
`
